import logo from './platzi.webp';
import react from 'react';
import { TodoBox } from './TodoBox';
import { TodoTitle } from './TodoTitle';
import { TodoSearch } from './TodoSearch';
import { TodoList } from './TodoList';
import { TodoItem } from './TodoItem';
import { CreateTodoButton } from './CreateTodoButton';
import './App.css';

const defaulTodos = [
  {text: 'Cortar cebolla', completed:false},
  {text: 'Tomar el curso de Intro a React.js', completed:false},
  {text: 'llorar con la llorona', completed:false},
  {text: 'otro todo', completed:false},
];

function App() {
  document.body.style.backgroundImage = 'radial-gradient(circle at 50% 51.01%, #f6ffff 0, #e9fffb 12.5%, #dbfff6 25%, #cbfdf1 37.5%, #b9f6ed 50%, #a7efea 62.5%, #95e9e9 75%, #84e3e9 87.5%, #74ddea 100%)';
  document.body.style.display = 'flex';
  document.body.style.alignItems = 'center';
  document.body.style.justifyContent = 'center';
  document.body.style.padding = '150px';
  return (
    <>
      <div>
          <TodoTitle completed={16} total={25}/>
          <TodoSearch/>
          <CreateTodoButton/>

          <TodoList> 
            {defaulTodos.map(todo => (
              <TodoItem
              key={todo.text} 
              text={todo.text}
              completed={todo.completed}
              />
            ))}
        </TodoList>
      </div>
    </>
  );
}
export default App;
